INSERT INTO `option` VALUES (1, 'Giới thiệu chung', 'Với phương châm hoạt động đạo đức, chuyên nghiệp và sáng tạo, chúng tôi tạo ra môi trường làm việc ý nghĩa cho nhân viên, mang đến khách hàng các giá trị cách tân và dịch vụ tuyệt hảo, hướng tới nâng cao giá trị cho cổ đông và có trách nhiệm với cộng đồng', 'web_about', NULL, NULL);
INSERT INTO `option` VALUES (2, 'Sứ mệnh', 'TLN là nhà cung cấp giải pháp và dịch vụ tổng thể về CNTT và truyền thông. Với phương châm hoạt động ĐẠO ĐỨC, CHUYÊN NGHIỆP VÀ SÁNG TẠO, chúng tôi tạo ra môi trường làm việc ý nghĩa cho nhân viên, mang đến khách hàng các giá trị cách tân và dịch vụ tuyệt ', 'web_about', NULL, NULL);
INSERT INTO `option` VALUES (3, 'Tầm nhìn', 'TLN là một tổ chức của những con người cùng đồng tâm hợp lực đóng góp chất xám và nỗ lực để dẫn đầu trong lĩnh vực kinh doanh tri thức đóng góp vào quá trình đưa Việt Nam trở thành nước tiên tiến trong nền kinh tế tri thức toàn cầu.', 'web_about', NULL, NULL);
INSERT INTO `option` VALUES (5, 'Phát triển phần mềm', 'Ứng dụng mô hình khởi nghiệp Lean startup và Agile, hợp tác thành công với rất nhiều start-up công nghệ và các công ty lớn để phát triển các dự án phần mềm yêu cầu tốc độ, đổi mới và sáng tạo', 'activity', NULL, ' fa fa-rocket');
INSERT INTO `option` VALUES (6, 'Phát triển doanh nghiệp', 'Xây dựng các sản phẩm của riêng mình và tìm kiếm cơ hội phát hành nó như một start-up độc lập Đầu tư, hỗ trợ và xây dựng business mới với 06 startup tại Nhật Bản và 01 startup tại Bangladesh. Đây đều là các start-up về các mang công nghệ đặc thù như AI,', 'activity', NULL, ' fa fa-location-arrow');
INSERT INTO `option` VALUES (7, 'Phát triển con người', 'Đào tạo đội ngũ IT Toàn cầu thông qua hợp tác với các trường đại học hàng đầu Châu Á. Khuyến khích và phát triển cộng đồng.', 'activity', NULL, 'fa fa-group');
INSERT INTO `option` VALUES (8, 'Đội ngũ sáng tạo', 'Chú trọng teamwork - Cởi mở tư duy - Không ngừng thử thách - Suy nghĩ tích cực - Đẩy nhanh tốc độ - Ý thức chuyên nghiệp - Giữ gìn bản chất', 'title', 2, NULL);
INSERT INTO `option` VALUES (9, 'Về chúng tôi', 'TLN là nhà cung cấp các giải pháp và dịch vụ công nghệ thông tin – viễn thông khép kín và toàn diện cho mọi khách hàng trên toàn thế giới.', 'title', 1, NULL);
INSERT INTO `option` VALUES (10, 'Tôn chỉ hoạt động', 'Tập trung khai thác sức mạnh của tri thức CNTT góp phần tăng cường năng lực cạnh tranh toàn cầu của các doanh nghiệp và tổ chức Việt Nam.\r\nHợp lực để tạo ra thành công cho các CIO trong từng dự án.\r\nNỗ lực trong từng tác vụ hướng tới dịch vụ CNTT chất lượng', 'about_us_1', 1, 'fa fa-desktop');
INSERT INTO `option` VALUES (11, 'Giá trị cốt lõi', 'Là một công ty công nghệ, Tùng Vân xác định giá trị con người là cốt lõi. Chúng tôi lấy lực lượng trẻ làm trung tâm và sản phẩm là câu trả lời để hiện thực hóa khát vọng đó.\r\nHãy để sản phẩm - thành quả lao động của bạn - làm thỏa mãn tất cả 90 triệu đồng bào người Việt trong và ngoài nước', 'about_us_1', 1, 'fa fa-eye');
