INSERT INTO `posts` VALUES (51, '1bb74cde-a3e7-404c-812b-6b82c82e3c37', 1, 'vietnamese', 2, 40, 0, ' LoveTV Viettel', 'lovetv-viettel', 'uploads/Posts/2018-06-19/lovetv/6c4af6e60829474dde9f8898868c436e.jpg', 'uploads/Posts/2018-06-19/lovetv/6c4af6e60829474dde9f8898868c436e_thumb.jpg', ' LoveTV', '<p>Fanpage LoveTV là kênh thông tin giải trí hàng đầu Việt Nam với những video clip, những thông tin đặc sắc về các vấn đề tình yêu, hôn nhân, gia đình... từ các chương trình của MCV Media sản xuất như: Bạn muốn hẹn hò, vợ chồng son...…</p>\r\n\r\n<p>……</p>\r\n', ' LoveTV Viettel', ' LoveTV Viettel', ' LoveTV', ' LoveTV Viettel', 0, 15, '2018-06-19 07:03:47', '2018-06-26 16:20:16', '', '', 0, 2);
INSERT INTO `posts` VALUES (58, '575ca1c4-fc9e-45f1-8043-f0c8330809f3', 1, 'vietnamese', 2, 0, 0, 'SCTV Phim Viettel', 'sctv-phim-viettel', 'assets/images/system/no_avatar.jpg', 'assets/images/system/no_avatar_100x100.jpg', 'SCTV Phim Viettel', '<p>1    GIỚI THIỆU<br>\r\nSCTV Phim là dịch vụ giá trị gia tăng trên mạng Vinaphone, Viettel, giúp khách hàng có thể xem trực tiếp các kênh truyền hình SD, HD của truyền hình cáp SCTV; và các bộ phim điện ảnh, phim truyền hình mới nhất, đặc sắc nhất ngay trên các thiết bị di động. <br>\r\nVới SCTV Phim, bạn được MIỄN PHÍ HOÀN TOÀN cước dữ liệu (3G/4G). Vì thế, đừng bỏ lỡ bất cứ bộ phim hấp dẫn nào tại http://sphim.tv hoặc qua ứng dụng SCTV Phim (hỗ trợ Android và IOS).</p>\r\n\r\n<p><br>\r\n2    ĐỐI TƯỢNG VÀ ĐIỀU KHOẢN SỬ DỤNG<br>\r\n-    Thuê bao đã đăng ký sử dụng dịch vụ Mobile Internet đang trong vùng phủ sóng GPRS/EDGE/3G/4G.<br>\r\n-    Thuê bao là thuê bao mạng di động Viettel, Vinaphone và đang hoạt động 2 chiều.<br>\r\n-    Thuê bao có thể nhận được các tin nhắn SMS.</p>\r\n', 'SCTV Phim Viettel', 'SCTV Phim Viettel', '', '', 0, 15, '2018-06-26 05:55:10', '2018-06-26 05:55:10', '', '', 0, 0);
