INSERT INTO `recently_action` VALUES (2397, 15, 'Trang', 'Pages', 'edit', '{\n    \"menu\": \"4\",\n    \"name\": \"D\\u1ecbch v\\u1ee5 thi\\u1ebft k\\u1ebf WEB\",\n    \"slugs\": \"dich-vu-thiet-ke-web\",\n    \"title\": \"D\\u1ecbch v\\u1ee5 thi\\u1ebft k\\u1ebf WEB\",\n    \"summary\": \"D\\u1ecbch v\\u1ee5 thi\\u1ebft k\\u1ebf WEB\",\n    \"content\": \"<h1><a href=\\\"https:\\/\\/www.creativevietnam.vn\\/dich-vu-thiet-ke-web\\/100-thit-k-web-may-tinh-k-thut-s\\\">Thi\\u1ebft K\\u1ebf Web M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1<\\/a><\\/h1>\\r\\n\\r\\n<dl>\\r\\n <dd>S\\u1ed0 TRUY C\\u1eacP: 6343<\\/dd>\\r\\n<\\/dl>\\r\\n\\r\\n<p><img alt=\\\"Thi\\u1ebft k\\u1ebf website m\\u00e1y t\\u00ednh - k\\u1ef9 thu\\u1eadt s\\u1ed1\\\" border=\\\"0\\\" src=\\\"https:\\/\\/www.creativevietnam.vn\\/images\\/stories\\/goi-thiet-ke-web\\/moi\\/banner-thiet-ke-web-ban-may-tinh-web-ky-thuat-so.jpg\\\" title=\\\"Thi\\u1ebft k\\u1ebf website m\\u00e1y t\\u00ednh- k\\u1ef9 thu\\u1eadt s\\u1ed1\\\"><\\/p>\\r\\n\\r\\n<p>M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1 l\\u00e0 c\\u00e1c s\\u1ea3n ph\\u1ea9m mang t\\u00ednh c\\u00f4ng ngh\\u1ec7 cao v\\u00e0 hi\\u1ec7n \\u0111\\u1ea1i. Do v\\u1eady, n\\u1ebfu b\\u1ea1n l\\u00e0 m\\u1ed9t doanh nghi\\u1ec7p \\u0111ang cung c\\u1ea5p s\\u1ea3n ph\\u1ea9m M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1 m\\u00e0 l\\u1ea1i ch\\u01b0a trang b\\u1ecb cho m\\u00ecnh 1 website \\u0111\\u1ec3 qu\\u1ea3ng b\\u00e1 s\\u1ea3n ph\\u1eabm th\\u00ec th\\u1eadt l\\u00e0 v\\u00f4 c\\u00f9ng thi\\u1ebfu s\\u00f3t. Qua \\u0111\\u00f3, kh\\u00e1ch h\\u00e0ng s\\u1ebd \\u0111\\u00e1nh gi\\u00e0 b\\u1ea1n l\\u00e0 m\\u1ed9t doanh nghi\\u1ec7p c\\u1ed5 h\\u1ee7, kh\\u00f4ng theo k\\u1ecbp th\\u1eddi \\u0111\\u1ea1i, d\\u1eabn \\u0111\\u1ebfn vi\\u1ec7c kinh doanh c\\u1ee7a b\\u1ea1n s\\u1ebd b\\u1ecb t\\u1ee5t d\\u1ed1c. V\\u1eady th\\u00ec c\\u00f2n ch\\u1edd g\\u00ec n\\u1eefa, h\\u00e3y tham kh\\u1ea3o qua c\\u00e1c g\\u1ee3i \\u00fd v\\u1ec1 website trong l\\u0129nh v\\u1ef1c M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1 c\\u1ee7a ch\\u00fang t\\u00f4i \\u0111\\u1ec3 b\\u1ea1n c\\u00f3 th\\u1ec3 d\\u1ec5 d\\u00e0ng l\\u00ean k\\u1ebf ho\\u1ea1ch cho vi\\u1ec7c x\\u00e2y d\\u1ef1ng 1 website, h\\u00e3y\\u00a0<em>thi\\u1ebft k\\u1ebf web<\\/em>\\u00a0<em>M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1<\\/em>\\u00a0chuy\\u00ean nghi\\u1ec7p ngay l\\u1eadp t\\u1ee9c.<\\/p>\\r\\n\\r\\n<p>C\\u00f4ng ty thi\\u1ebft k\\u1ebf web Creative Vi\\u1ec7t nam s\\u1ebd gi\\u00fap b\\u1ea1n T<strong>hi\\u1ebft k\\u1ebf web M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1<\\/strong>\\u00a0nhanh nh\\u1ea5t, ph\\u00f9 h\\u1ee3p v\\u1edbi c\\u00f4ng c\\u1ee5 t\\u00ecm ki\\u1ebfm \\u0111\\u1ec3 web c\\u1ee7a b\\u1ea1n \\u0111\\u01b0\\u1ee3c xu\\u1ea5t hi\\u1ec7n nhanh tr\\u00ean c\\u00e1c c\\u00f4ng c\\u1ee5 t\\u00ecm ki\\u1ebfm<\\/p>\\r\\n\\r\\n<p>\\u00a0<\\/p>\\r\\n\\r\\n<p>\\u00a0<\\/p>\\r\\n\\r\\n<table align=\\\"center\\\" border=\\\"1\\\" cellpadding=\\\"5\\\" cellspacing=\\\"0\\\">\\r\\n <tbody>\\r\\n  <tr>\\r\\n   <td colspan=\\\"2\\\"><strong>Di\\u1ec5n Gi\\u1ea3i Ch\\u1ee9c N\\u0103ng<\\/strong><\\/td>\\r\\n   <td align=\\\"center\\\" valign=\\\"top\\\"><strong>Qu\\u1ea3n Tr\\u1ecb Web<\\/strong><\\/td>\\r\\n  <\\/tr>\\r\\n  <tr>\\r\\n   <td align=\\\"center\\\"><strong>01<\\/strong><\\/td>\\r\\n   <td colspan=\\\"2\\\"><strong>TRANG CH\\u1ee6<\\/strong><\\/td>\\r\\n  <\\/tr>\\r\\n  <tr>\\r\\n   <td colspan=\\\"2\\\">\\r\\n   <ul>\\r\\n    <li>Tr\\u00ecnh b\\u00e0y trang nh\\u00e3, \\u0111\\u01a1n gi\\u1ea3n, th\\u00e2n thi\\u1ec7n<\\/li>\\r\\n    <li>M\\u00e0u s\\u1eafc theo tone m\\u00e0u c\\u1ee7a Kh\\u00e1ch h\\u00e0ng y\\u00eau c\\u1ea7u ho\\u1eb7c theo m\\u00e0u logo c\\u1ee7a c\\u00f4ng ty b\\u00e1n M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1.<\\/li>\\r\\n    <li>Thanh menu ch\\u00ednh c\\u1ee7a website M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1.<\\/li>\\r\\n    <li>N\\u1ed9i dung hi\\u1ec3n th\\u1ecb ch\\u1ee7 y\\u1ebfu: h\\u00ecnh \\u1ea3nh & n\\u1ed9i dung gi\\u1edbi thi\\u1ec7u v\\u1ec1 c\\u00f4ng ty b\\u00e1n M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1.<\\/li>\\r\\n    <li>C\\u00f3 banner \\u0111\\u1ed5i h\\u00ecnh l\\u00e0m b\\u1eb1ng hi\\u1ec7u \\u1ee9ng Flash \\/ JavaScript chuy\\u1ec3n \\u0111\\u1ed9ng nh\\u1eb9 nh\\u00e0ng.<\\/li>\\r\\n   <\\/ul>\\r\\n   <\\/td>\\r\\n   <td valign=\\\"middle\\\">C\\u00e1c module n\\u1ed9i dung v\\u00e0 menu ch\\u00ednh c\\u00f3 th\\u1ec3 t\\u1eaft m\\u1edf h\\u1eb7c thay \\u0111\\u1ed5i t\\u00ean menu linh ho\\u1ea1t qua h\\u1ec7 qu\\u1ea3n tr\\u1ecb.<\\/td>\\r\\n  <\\/tr>\\r\\n  <tr>\\r\\n   <td align=\\\"center\\\"><strong>02<\\/strong><\\/td>\\r\\n   <td colspan=\\\"2\\\"><strong>GI\\u1edaI THI\\u1ec6U C\\u00d4NG TY B\\u00c1N M\\u00c1Y T\\u00cdNH - K\\u1ef8 THU\\u1eacT S\\u1ed0<\\/strong><\\/td>\\r\\n  <\\/tr>\\r\\n  <tr>\\r\\n   <td colspan=\\\"2\\\"><br>\\r\\n   N\\u1ed9i dung gi\\u1edbi thi\\u1ec7u v\\u1ec1 C\\u00f4ng ty b\\u00e1n M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1 nh\\u01b0:\\r\\n   <ul>\\r\\n    <li>Gi\\u1edbi thi\\u1ec7u chung v\\u1ec1 c\\u00f4ng ty b\\u00e1n M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1.<\\/li>\\r\\n    <li>N\\u0103ng l\\u1ef1c, \\u0111\\u1ed9i ng\\u0169 nh\\u00e2n s\\u1ef1.<\\/li>\\r\\n    <li>N\\u1ed9i dung hi\\u1ec3n th\\u1ecb d\\u1ea1ng b\\u00e0i vi\\u1ebft, c\\u00f3 th\\u1ec3 ch\\u00e8n h\\u00ecnh \\u1ea3nh minh h\\u1ecda.<\\/li>\\r\\n    <li>Kh\\u00f4ng gi\\u1edbi h\\u1ea1n c\\u00e1c ph\\u00e2n m\\u1ee5c.<\\/li>\\r\\n   <\\/ul>\\r\\n   <\\/td>\\r\\n   <td valign=\\\"middle\\\">Qu\\u1ea3n tr\\u1ecb c\\u00f3 th\\u1ec3 th\\u00eam, b\\u1edbt, x\\u00f3a, s\\u1eeda c\\u00e1c n\\u1ed9i dung b\\u00e0i vi\\u1ebft v\\u00e0 h\\u00ecnh \\u1ea3nh.<\\/td>\\r\\n  <\\/tr>\\r\\n  <tr>\\r\\n   <td align=\\\"center\\\"><strong>03<\\/strong><\\/td>\\r\\n   <td colspan=\\\"2\\\"><strong>S\\u1ea2N PH\\u1ea8M M\\u00c1Y T\\u00cdNH - K\\u1