INSERT INTO `recently_action` VALUES (2399, 15, 'Trang', 'Pages', 'edit', '{\n    \"menu\": \"4\",\n    \"name\": \"D\\u1ecbch v\\u1ee5 VAS\",\n    \"slugs\": \"dich-vu-vas\",\n    \"title\": \"D\\u1ecbch v\\u1ee5 VAS\",\n    \"summary\": \"D\\u1ecbch v\\u1ee5 VAS\",\n    \"content\": \"<header>\\r\\n<h1>D\\u1ecbch v\\u1ee5 gi\\u00e1 tr\\u1ecb gia t\\u0103ng kh\\u00e1ch h\\u00e0ng doanh nghi\\u1ec7p<\\/h1>\\r\\n<\\/header>\\r\\n\\r\\n<p>\\u00a0<\\/p>\\r\\n\\r\\n<h2><strong>D\\u1ecbch v\\u1ee5 gi\\u00e1 tr\\u1ecb gia t\\u0103ng<\\/strong>\\u00a0(vi\\u1ebft t\\u1eaft VAS, ti\\u1ebfng anh Value-added service) l\\u00e0 thu\\u1eadt ng\\u1eef kh\\u00e1 ph\\u1ed5 bi\\u1ebfn d\\u00f9ng trong l\\u0129nh v\\u1ef1c c\\u00f4ng nghi\\u1ec7p vi\\u1ec5n th\\u00f4ng. D\\u1ecbch v\\u1ee5 gi\\u00e1 tr\\u1ecb gia t\\u0103ng \\u0111\\u01b0\\u1ee3c \\u0111\\u1ecbnh ngh\\u0129a l\\u00e0 nh\\u1eefng d\\u1ecbch v\\u1ee5 ngo\\u00e0i g\\u1ecdi, fax<\\/h2>\\r\\n\\r\\n<p>\\u0110\\u1ed1i v\\u1edbi \\u0111i\\u1ec7n tho\\u1ea1i di \\u0111\\u1ed9ng th\\u00ec d\\u1ecbch v\\u1ee5 ngo\\u00e0i g\\u1ecdi (tho\\u1ea1i) th\\u00ec c\\u00e1c d\\u1ecbch v\\u1ee5 kh\\u00e1c v\\u00ed d\\u1ee5 SMS, nh\\u1ea1c ch\\u1edd, c\\u00e1c d\\u1ecbch v\\u1ee5 tr\\u00ean n\\u1ec1n Data nh\\u01b0 GPRS hay 3g...\\u0111i\\u1ec1u \\u0111\\u01b0\\u1ee3c xem l\\u00e0 d\\u1ecbch v\\u1ee5 gi\\u00e1 tr\\u1ecb gia t\\u0103ng.\\\"<\\/p>\\r\\n\\r\\n<p>http:\\/\\/vi.wikipedia.org\\/wiki\\/D\\u1ecbch_v\\u1ee5_gi\\u00e1_tr\\u1ecb_gia_t\\u0103ng<\\/p>\\r\\n\\r\\n<p>\\u0110\\u1ed1i v\\u1edbi C\\u00f4ng ty Vi\\u1ec5n th\\u00f4ng Di \\u0111\\u1ed9ng, D\\u1ecbch v\\u1ee5 gi\\u00e1 tr\\u1ecb gia t\\u0103ng mang m\\u1ed9t \\u00fd ngh\\u0129a kh\\u00e1c h\\u1eb3n. \\u0110\\u00f3 l\\u00e0 nh\\u1eefng gi\\u00e1 tr\\u1ecb c\\u1ed9ng th\\u00eam mang \\u0111\\u1ebfn cho Kh\\u00e1ch h\\u00e0ng, Kh\\u00e1ch h\\u00e0ng \\u0111\\u01b0\\u1ee3c h\\u01b0\\u1edfng khi s\\u1eed d\\u1ee5ng m\\u1ed9t d\\u1ecbch v\\u1ee5\\/s\\u1ea3n ph\\u1ea9m n\\u00e0o \\u0111\\u00f3<\\/p>\\r\\n\\r\\n<p>- Kh\\u00e1ch h\\u00e0ng \\u0103n m\\u1ed9t m\\u00f3n \\u0103n \\u1edf qu\\u00e1n \\u0103n, \\u0111\\u01b0\\u1ee3c mi\\u1ec5n ph\\u00ed tr\\u00e0 \\u0111\\u00e1, kh\\u0103n l\\u1ea1nh<\\/p>\\r\\n\\r\\n<p>- Kh\\u00e1ch h\\u00e0ng shopping, \\u0111\\u01b0\\u1ee3c t\\u1eb7ng voucher gi\\u1ea3m gi\\u00e1, t\\u1eb7ng th\\u1ebb th\\u00e0nh vi\\u00ean<\\/p>\\r\\n\\r\\n<p>- Kh\\u00e1ch h\\u00e0ng mua s\\u1eefa cho gia \\u0111\\u00ecnh, \\u0111\\u01b0\\u1ee3c t\\u00edch l\\u0169y \\u0111i\\u1ec3m,..<\\/p>\\r\\n\\r\\n<p>- Kh\\u00e1ch h\\u00e0ng \\u0111\\u1eb7t v\\u00e9 m\\u00e1y bay, \\u0111\\u01b0\\u1ee3c nh\\u1eafc nh\\u1edf l\\u1ecbch bay, th\\u00f4ng b\\u00e1o l\\u1ecbch tr\\u00ecnh bay, ch\\u00fac m\\u1eebng sinh nh\\u1eadt,..<\\/p>\\r\\n\\r\\n<p>- S\\u1eed d\\u1ee5ng s\\u1ea3n ph\\u1ea9m, c\\u00f3 c\\u01a1 h\\u1ed9i b\\u1ed1c th\\u0103m may m\\u1eafn,..<\\/p>\\r\\n\\r\\n<p><em><img alt=\\\"d\\u1ecbch v\\u1ee5 gi\\u00e1 tr\\u1ecb gia t\\u0103ng\\\" src=\\\"http:\\/\\/115.84.182.218:8080\\/imagesvtdd\\/1402300080388.jpg\\\"><\\/em><\\/p>\\r\\n\\r\\n<p>C\\u0169ng l\\u00e0 b\\u00e1n ra m\\u1ed9t s\\u1ea3n ph\\u1ea9m, d\\u1ecbch v\\u1ee5, nh\\u01b0ng ch\\u00fang ta ho\\u00e0n to\\u00e0n t\\u1ea1o ra nh\\u1eefng gi\\u00e1 tr\\u1ecb gia t\\u0103ng c\\u1ed9ng th\\u00eam \\u0111\\u1ec3 mang l\\u1ea1i cho kh\\u00e1ch h\\u00e0ng s\\u1ef1 h\\u1ee9ng th\\u00fa, y\\u00eau m\\u1ebfn v\\u00e0 quay l\\u1ea1i s\\u1eed d\\u1ee5ng ti\\u1ebfp. Quan tr\\u1ecdng h\\u01a1n, c\\u00e1c gi\\u00e1 tr\\u1ecb c\\u1ed9ng th\\u00eam n\\u00e0y, \\u0111\\u1ebfn tay ng\\u01b0\\u1eddi d\\u00f9ng m\\u1ed9t c\\u00e1ch thu\\u1eadn ti\\u1ec7n, nhanh g\\u1ecdn, tin c\\u1eady, minh b\\u1ea1ch<\\/p>\\r\\n\\r\\n<p>-Mua qu\\u1ea7n \\u00e1o, \\u0111\\u01b0\\u1ee3c nh\\u1eadn voucher gi\\u1ea3m gi\\u00e1 ch\\u1ec9 qua tin nh\\u1eafn, ho\\u1eb7c l\\u1eddi ch\\u00fac nh\\u00e2n d\\u1ecbp sinh nh\\u1eadt - s\\u1eed d\\u1ee5ng tin nh\\u1eafn th\\u00f4ng th\\u01b0\\u1eddng ho\\u1eb7c\\u00a0<strong><a href=\\\"http:\\/\\/vienthongdidong.vn\\/brandname-sms-tin-nhan-thuong-hieu\\\">tin nh\\u1eafn th\\u01b0\\u01a1ng hi\\u1ec7u<\\/a><\\/strong><\\/p>\\r\\n\\r\\n<p>-Mua s\\u1eefa h\\u00e0ng ng\\u00e0y cho con, g\\u1eedi tin nh\\u1eafn nh\\u1eadn ngay l\\u1eadp t\\u1ee9c ph\\u1ea7n qu\\u00e0, ho\\u1eb7c t\\u00edch l\\u0169y \\u0111i\\u1ec3m nh\\u01b0 \\u0111i mua h\\u00e0ng trong si\\u00eau th\\u1ecb<\\/p>\\r\\n\\r\\n<p>-T\\u1ed5 ch\\u1ee9c c\\u00e1c ch\\u01b0\\u01a1ng tr\\u00ecnh quay s\\u1ed1 may m\\u1eafn cho kh\\u00e1ch h\\u00e0ng khi mua h\\u00e0ng<\\/p>\\r\\n\\r\\n<p>V\\u00e0 v\\u00f4 s\\u1ed1 v\\u00ed d\\u1ee5 kh\\u00e1c v\\u1ec1 gi\\u00e1 tr\\u1ecb c\\u1ed9ng th\\u00eam. Nh\\u01b0ng \\u0111\\u1ee9ng v\\u1ec1 g\\u00f3c nh\\u00ecn c\\u1ee7a C\\u00f4ng ty Vi\\u1ec5n th\\u00f4ng Di \\u0111\\u1ed9ng, ch\\u00fang t\\u00f4i c\\u00f3 nh\\u1eefng c\\u00f4ng c\\u1ee5, c\\u00f3 th\\u1ec3 g\\u1ecdi l\\u00e0 tr\\u1ee3 th\\u1ee7 \\u0111\\u1eafc l\\u1ef1c cho Qu\\u00fd Kh\\u00e1ch h\\u00e0ng, Qu\\u00fd \\u0111\\u1ed1i t\\u00e1c t\\u1ea1o ra gi\\u00e1 tr\\u1ecb c\\u1ed9ng th\\u00eam \\u0111\\u00f3, th\\u1ef1c hi\\u1ec7n c\\u00e1c chi\\u1ebfn d\\u1ecbch Digital Marketing, c\\u00e1c\\u00a0<a href=\\\"http:\\/\\/vienthongdidong.vn\\/giai-phap-marketing-cho-doanh-nghiep\\\"><strong>gi\\u1ea3i ph\\u00e1p Marketing<\\/strong><\\/a>\\u00a0cho nh\\u00e3n h\\u00e0ng, doanh nghi\\u1ec7p. T\\u1ea5t c\\u1ea3 v\\u00ec muc \\u0111\\u00ednh ph\\u1ee5c v\\u1ee5 Kh\\u00e1ch h\\u00e0ng tr\\u1ef1c ti\\u1ebfp, mang l\\u1ea1i s\\u1ef1 th\\u1ecfa m\\u00e3n t\\u1ed1i \\u0111a khi Kh\\u00e1ch h\\u00e0ng, ng\\u01b0\\u1eddi ti\\u00eau d\\u00f9ng s\\u1eed d\\u1ee5ng s\\u1ea3n ph\\u1ea9m, d\\u1ecbch v\\u1ee5 c\\u1ee7a Qu\\u00fd \\u0110\\u1ed1i t\\u00e1c<\\/p>\\r\\n\\r\\n<p>M\\u1ecdi chi ti\\u1ebft xin li\\u00ean h\\u1ec7 \\u0111\\u1ec3 c\\u00f9ng ti\\u1ebfn t\\u1edbi m\\u1ed9t gi\\u1ea3i ph\\u00e1p, d\\u1ef1 \\u00e1n thi\\u1ebft th\\u1ef1c<\\/p>\\r\\n\",\n    \"description\": \"D\\u1ecbch v\\u1e