INSERT INTO `recently_action` VALUES (2466, 15, 'Trang', 'Pages', 'edit', '{\n    \"menu\": \"4\",\n    \"name\": \"D\\u1ecbch v\\u1ee5 thi\\u1ebft k\\u1ebf WEB\",\n    \"slugs\": \"dich-vu-thiet-ke-web\",\n    \"title\": \"D\\u1ecbch v\\u1ee5 thi\\u1ebft k\\u1ebf WEB\",\n    \"summary\": \"D\\u1ecbch v\\u1ee5 thi\\u1ebft k\\u1ebf WEB\",\n    \"content\": \"<h1><a href=\\\"https:\\/\\/www.creativevietnam.vn\\/dich-vu-thiet-ke-web\\/100-thit-k-web-may-tinh-k-thut-s\\\">Thi\\u1ebft K\\u1ebf Web M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1<\\/a><\\/h1>\\r\\n\\r\\n<dl>\\r\\n <dd>S\\u1ed0 TRUY C\\u1eacP: 6343<\\/dd>\\r\\n<\\/dl>\\r\\n\\r\\n<p><img alt=\\\"Thi\\u1ebft k\\u1ebf website m\\u00e1y t\\u00ednh - k\\u1ef9 thu\\u1eadt s\\u1ed1\\\" border=\\\"0\\\" src=\\\"https:\\/\\/www.creativevietnam.vn\\/images\\/stories\\/goi-thiet-ke-web\\/moi\\/banner-thiet-ke-web-ban-may-tinh-web-ky-thuat-so.jpg\\\" title=\\\"Thi\\u1ebft k\\u1ebf website m\\u00e1y t\\u00ednh- k\\u1ef9 thu\\u1eadt s\\u1ed1\\\"><\\/p>\\r\\n\\r\\n<p>M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1 l\\u00e0 c\\u00e1c s\\u1ea3n ph\\u1ea9m mang t\\u00ednh c\\u00f4ng ngh\\u1ec7 cao v\\u00e0 hi\\u1ec7n \\u0111\\u1ea1i. Do v\\u1eady, n\\u1ebfu b\\u1ea1n l\\u00e0 m\\u1ed9t doanh nghi\\u1ec7p \\u0111ang cung c\\u1ea5p s\\u1ea3n ph\\u1ea9m M\\u00e1y t\\u00ednh - K\\u1ef9 thu\\u1eadt s\\u1ed1 m\\u00e0 l\\u1ea1i ch\\u01b0a trang b\\u1ecb cho m\\u00ecnh 1 website \\u0111\\u1ec3 qu\\u1ea3ng b\\u00e1 s\\u1ea3n ph\\u1eabm th\\u00ec th\\u1eadt l\\u00e0 v\\u00f4 c\\u00f9ng thi\\u1ebfu s\\u00f3t. Qua \\u0111\\u00f3, kh\\u00e1ch h\\u00e0ng s\\u1ebd \\u0111\\u00e1nh gi\\u00e0 b\\u1ea1n l\\u00e0 m\\u1ed9t doanh nghi\\u1ec7p c\\u1ed5 h\\u1ee7, kh\\u00f4ng theo k\\u1ecbp th\\u1eddi \\u0111\\u1ea1i, d\\u1eabn \\u0111\\u1ebfn vi\\u1ec7c kinh doanh c\\u1ee7a b\\u1ea1n s\\u1ebd b\\u1ecb t\\u1ee5t d\\u1ed1c. V\\u1eady th\\u00ec c\\u00f2n ch\\u1edd g\\u00ec n\\u1eefa, h\\u00e3y tham kh\\u1ea3o qua c\\u00e1c g\\u1ee3i \\u00f